package Lec5;

public class For_Loop_Demo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int i = 1; i <= 5; i++) {
			System.out.print(i + " ");

		}

		for (int i = 1; i <= 5;) {
			System.out.print(i + " ");
			i++;

		}
		int i = 1;
		for (; i <= 5;) {
			System.out.print(i + " ");
			i++;

		}

	}

}
